function run {
    ./source/avalanche.py --random-seed=0 "${@}";
}

function test_avalanche_p1e3 {
    assertEquals "$(run -p1000)" "$matrix_e00" ;
}

function test_avalanche_p1e3_e00 {
    assertEquals "$(run -p1000 -R20 -s01 -S20 -q0.70 -e0.00)" "$matrix_e00" ;
}

