function run {
    ./source/avalanche.py --random-seed=0 "${@}";
}

function test_avalanche_p1e5 {
    assertEquals "$(run -p100000)" "$matrix_e00" ;
}

function test_avalanche_p1e5_e00 {
    assertEquals "$(run -p100000 -R20 -s01 -S20 -q0.70 -e0.00)" "$matrix_e00" ;
}

