function run {
    ./source/avalanche.py --random-seed=0 "${@}";
}

function test_avalanche_p1e6 {
    assertEquals "$(run -p1000000 -s20)" "$matrix_e00" ;
}

function test_avalanche_p1e6_e00 {
    assertEquals "$(run -p1000000 -R20 -s20 -S20 -q0.70 -e0.00)" "$matrix_e00" ;
}

function test_avalanche_p1e6_e15 {
    assertEquals "$(run -p1000000 -R20 -s20 -S20 -q0.70 -e0.15)" "$matrix_e15" ;
}

function test_avalanche_p1e6_e30 {
    assertEquals "$(run -p1000000 -R20 -s20 -S20 -q0.70 -e0.30)" "$matrix_e30" ;
}

function test_avalanche_p1e6_e45 {
    assertEquals "$(run -p1000000 -R20 -s20 -S20 -q0.70 -e0.45)" "$matrix_e45" ;
